import 'package:flutter/material.dart';import 'package:template/dashboard_screen.dart';import 'package:template/eleave_screen.dart';import 'package:template/login_screen.dart';import 'package:template/report_screen.dart';class HomeScreen extends StatefulWidget {  const HomeScreen({Key? key}) : super(key: key);  @override  State<HomeScreen> createState() => _HomeScreenState();}class _HomeScreenState extends State<HomeScreen> {  int _selectedIndex = 0;  void _onItemTapped(int index){    setState((){      _selectedIndex= index;    });  }  Widget _body(){    if(_selectedIndex == 0){      return const DashboardScreen();    } else if(_selectedIndex == 1){      return const EleaveScreen();    } else if(_selectedIndex == 2){      return const ReportScreen();    }    return const DashboardScreen();  }  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        backgroundColor: Colors.red,        title: const Text("Home"),        actions: [          IconButton(              onPressed: (){            Navigator.pushReplacement(context, MaterialPageRoute(builder: (context) => const LoginScreen()));          }, icon: const Icon(Icons.logout))        ],      ),      body: _body(),      bottomNavigationBar: BottomNavigationBar(        backgroundColor: Colors.red,        onTap: _onItemTapped,        currentIndex: _selectedIndex,        selectedItemColor: Colors.white,        items: const [          BottomNavigationBarItem(            icon: Icon(Icons.home, color: Colors.white,),            label: "Home",          ),          BottomNavigationBarItem(              icon: Icon(Icons.access_time, color: Colors.white,),              label: "E-Leave"          ),          BottomNavigationBarItem(              icon: Icon(Icons.report, color: Colors.white,),              label: "Report"          ),        ],      ),    );  }}