import 'package:flutter/material.dart';import 'package:template/home_screen.dart';class LoginScreen extends StatefulWidget {  const LoginScreen({Key? key}) : super(key: key);  @override  State<LoginScreen> createState() => _LoginScreenState();}class _LoginScreenState extends State<LoginScreen> {  final GlobalKey<FormState> _formState = GlobalKey<FormState>();  final TextEditingController _usernameController = TextEditingController();  final TextEditingController _passwordController = TextEditingController();  @override  Widget build(BuildContext context) {    return Scaffold(      body: Center(        child: SingleChildScrollView(          child: Form(            key: _formState,            child: Column(              mainAxisAlignment: MainAxisAlignment.center,              crossAxisAlignment: CrossAxisAlignment.center,              children: [                SizedBox(                  height: 75,                  child: Image.asset("assets/New_Logo_JNE.png")                ),                const SizedBox(height: 25),                Container(                  margin: const EdgeInsets.only(left: 15, right: 15),                  child: TextFormField(                    controller: _usernameController,                    decoration: const InputDecoration(                      hintText: "NIK",                      prefixIcon: Icon(Icons.supervised_user_circle_sharp),                    ),                    validator: (value){                      if(value!.isEmpty){                        return "Please enter the Field!";                      }                      return null;                    },                  ),                ),                const SizedBox(height: 25),                Container(                  margin: const EdgeInsets.only(left: 15, right: 15),                  child: TextFormField(                    obscureText: true,                    controller: _passwordController,                    decoration: const InputDecoration(                      hintText: "Password",                      prefixIcon: Icon(Icons.lock),                    ),                    validator: (value){                      if(value!.isEmpty){                        return "Please enter the Field!";                      }                      return null;                    },                  ),                ),                const SizedBox(height: 25),                ElevatedButton(                    style: ElevatedButton.styleFrom(                      primary: Colors.green,                    ),                    onPressed: (){                  if(_formState.currentState!.validate()){                    Navigator.pushReplacement(context, MaterialPageRoute(builder: (context) => const HomeScreen()));                  }                }, child: const Text("LOGIN"))              ],            ),          ),        ),      ),    );  }}